import { Component } from '@angular/core';
import { User } from '../model/User';
import { UserService } from 'src/app/services/User.service';
import { HttpErrorResponse } from '@angular/common/http';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';
@Component({
  selector: 'app-mdp',
  templateUrl: './mdp.component.html',
  styleUrls: ['./mdp.component.css']
})
export class MdpComponent {
  constructor( private service: UserService, private router:Router, private authService: AuthService) { }
  listUser!: User[];

  email!:string;
  motDePasse!:string;
  login(){
    let i=0;
    let test=false;
    while((i < this.listUser.length)&&(test==false)){
      if(this.listUser[i].email==this.email&&this.listUser[i].motDePasse==this.motDePasse){
        test=true
      }
      else i++;
    }
    if(test==false||this.email==""||this.motDePasse==""){
      alert("your email or password is invalid");
    }
    else{
      this.authService.setLoggedIn(true); // Notification de connexion réussie
      this.router.navigate(['profile',this.listUser[i].id])
    }
  }

 ngOnInit(){
     this.service.getAll().subscribe(
       { next: (data) => this.listUser=data,
         error: (err) => console.log(err),
         complete: () => console.log('done')
       });

   }   
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Login Page</title>
  <link rel="stylesheet" href="mdp.component.css">
</head>
<body>
  <div class="background"></div>
  <div class="login-container">
    <form class="login-form">
      <h2>Welcome Back!</h2>
      <div class="form-group">
        <label for="email"> Email Adresse  </label>
        <input type="email" id="email" name="email" placeholder="Enter your email adresse " [(ngModel)]="email" required>
      </div>
      <div class="form-group">
        <label for="password">numero tel</label>
        <input type="email" id="email" name="email" placeholder="Enter your email adresse " [(ngModel)]="email" required>
        <a href="#" class="forgot-password">Forgot Password?</a>
      </div>
      <button type="submit" (click)="login()">Login</button>
      <p class="register-link" routerLink="/register">Don't have an account? <a class="nav-link" routerLink="/register">Register</a></p>
    </form>
  </div>
</body>
</html>
