import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators, FormBuilder } from '@angular/forms';
import { UserService } from 'src/app/services/User.service';
import { HttpErrorResponse } from '@angular/common/http';
import { Role, User } from '../model/User';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  constructor(private formBuilder: FormBuilder, private service: UserService, private router:Router) { }
  listUser!: User[];
  photoUser:any;
  length:number=0;
   i:number=0;
  RegisterForm = new FormGroup({
    nomUtilisateur: new FormControl('', [Validators.required, Validators.minLength(3)]),
    prenomUtilisateur: new FormControl('', [Validators.required, Validators.minLength(3)]),
    email: new FormControl('', [Validators.required, Validators.email]),
    motDePasse: new FormControl('', [Validators.required, Validators.minLength(8),
       Validators.pattern('^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9]).*$')]),
    numTel: new FormControl('', [Validators.required, Validators.pattern('[0-9]{8}')]),
    role: new FormControl('', [Validators.required]),
    interet: new FormControl('', [Validators.required]),
    photo:new FormControl(null, [Validators.required]),
  });

  persistUser() {
    if (this.RegisterForm.valid) {
      const formData = this.RegisterForm.value;
      const UserData: any = {
        nomUtilisateur: formData.nomUtilisateur,
        prenomUtilisateur: formData.prenomUtilisateur,
        email: formData.email,
        numTel: formData.numTel,
        motDePasse: formData.motDePasse,
        role:formData.role,
        interet:formData.interet,
        photo: null, 
      };
      if (this.photoUser) {
        UserData.photo = this.photoUser.name; // Set photo only if an image is selected

      }
      this.service.ajouterUser(UserData,this.photoUser).subscribe(
        (response) => {

          this.router.navigate(['login'])

          console.log('Utilisateur ajoutée avec succès :', response);
          // Optionally, provide feedback to the user that registration was successful
        },
        (error: HttpErrorResponse) => {
          console.error('Erreur lors de l\'ajout de l\'utilisateur :', error);
          // Check the error status and handle it appropriately
          if (error.status === 400) {
            // Bad request error, handle validation errors or other issues
            // You can access error.error to get the detailed error message from the server
            // Provide feedback to the user about the error
          } else {
            // Handle other types of errors (e.g., server down, network error)
            // Provide appropriate feedback to the user
          }
        }
      );
    }
  }
  image(test:any){
    var reader = new FileReader();
    reader.readAsDataURL(test.target.files[0])
    this.photoUser = test.target.files[0];
  }
  ngOnInit(): void {
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Register Page</title>
  <link rel="stylesheet" href="register.component.css">
</head>
<body>
  <div class="register-container" style="margin-top: 100px;">
    <form [formGroup]="RegisterForm" (ngSubmit)="persistUser()">
      <h2>Register</h2>
      <div class="form-group">
        <label for="nomUtilisateur">Last Name <span>(Specify your last name)</span></label>
        <input type="text" id="nomUtilisateur" name="nomUtilisateur" placeholder="Enter your last name" formControlName="nomUtilisateur" required>
        <div *ngIf="RegisterForm.get('nomUtilisateur')?.invalid && RegisterForm.get('nomUtilisateur')?.touched">
          <div *ngIf="RegisterForm.get('nomUtilisateur')?.hasError('required')">Last name is required</div>
          <div *ngIf="RegisterForm.get('nomUtilisateur')?.hasError('minlength')">Last name should be at least 3 characters</div>
        </div>
      </div>
      <div class="form-group">
        <label for="prenomUtilisateur">First Name <span>(Specify your first name)</span></label>
        <input type="text" id="prenomUtilisateur" name="prenomUtilisateur" placeholder="Enter your first name" formControlName="prenomUtilisateur" required>
        <div *ngIf="RegisterForm.get('prenomUtilisateur')?.invalid && RegisterForm.get('prenomUtilisateur')?.touched">
          <div *ngIf="RegisterForm.get('prenomUtilisateur')?.hasError('required')">First name is required</div>
          <div *ngIf="RegisterForm.get('prenomUtilisateur')?.hasError('minlength')">First name should be at least 3 characters</div>
        </div>
      </div>
      <div class="form-group">
        <label for="email">Email Address <span>(Specify your email address)</span></label>
        <input type="email" id="email" name="email" placeholder="Enter your email address" formControlName="email" required>
        <div *ngIf="RegisterForm.get('email')?.invalid && RegisterForm.get('email')?.touched">
          <div *ngIf="RegisterForm.get('email')?.hasError('required')">Email is required</div>
          <div *ngIf="RegisterForm.get('email')?.hasError('email')">Email must respect the rules</div>
        </div>
      </div>
      <div class="form-group">
        <label for="motDePasse">Password <span>(Specify your password)</span></label>
        <input type="password" id="motDePasse" name="motDePasse" placeholder="Enter your password" formControlName="motDePasse" required>
        <div *ngIf="RegisterForm.get('motDePasse')?.invalid && RegisterForm.get('motDePasse')?.touched">
          <div *ngIf="RegisterForm.get('motDePasse')?.hasError('required')">Password is required</div>
          <div *ngIf="RegisterForm.get('motDePasse')?.hasError('minlength')">Password should be at least 8 characters</div>
          <div *ngIf="RegisterForm.get('motDePasse')?.hasError('pattern')">
            Password  must contain at least one uppercase letter, one lowercase letter, and one number</div>
        </div>
      </div>
      <div class="form-group">
        <label for="numTel">Phone Number <span>(Specify your phone number)</span></label>
        <input type="text" id="numTel" name="numTel" placeholder="Enter your phone number" formControlName="numTel" required>
        <div *ngIf="RegisterForm.get('numTel')?.invalid && RegisterForm.get('numTel')?.touched">
          <div *ngIf="RegisterForm.get('numTel')?.hasError('required')">Telephone is required</div>
          <div *ngIf="RegisterForm.get('numTel')?.hasError('pattern')">Telephone should be 8 numbers</div>
        </div>
      </div>
      <div class="form-group">
        <label for="role">Role <span>(Select your role)</span></label>
        <select id="role" name="role" formControlName="role" required>
          <option value="" disabled selected>Choose a role</option>
          <option value="super_admin">Super Admin</option>
          <option value="admin_club">Club Admin</option>
          <option value="user">User</option>
        </select>
      </div>
      <div class="form-group">
        <label for="interet">Interest <span>(Select your interest)</span></label>
        <select id="interet" name="interet" formControlName="interet"  required>
          <option value="" disabled selected>Choose an interest</option>
          <option value="sport">Sport</option>
          <option value="music">Music</option>
          <option value="tech">Technology</option>
          <option value="photographie">Photography</option>
          <option value="design">Design</option>
          <option value="dessin">Drawing</option>
          <option value="robots">Robots</option>
        </select>
      </div>
      <div class="form-group">
        <label for="photo">photo <span>(Specify your photo)</span></label>
        <input type="file" id="photo" name="photo" placeholder="Enter your photo" formControlName="photo" (change)="image($event)" required>
        <div *ngIf="RegisterForm.get('photo')?.invalid && RegisterForm.get('photo')?.touched">
          <div *ngIf="RegisterForm.get('photo')?.hasError('required')">Last name is required</div>
        </div>
      </div>
      <button type="submit"  >Register</button>
    </form>
  </div>

</body>
</html>
