import { Component, OnDestroy } from '@angular/core';
import { WebSocketSubject } from 'rxjs/webSocket';
@Component({
  selector: 'app-ia',
  templateUrl: './ia.component.html',
  styleUrls: ['./ia.component.css']
})
export class IAComponent  {
  cameraUrl: string = '';
  reply: string = '';
  private _channel!: WebSocketSubject<any>;



  sendUrlToPython() {
    this._channel = new WebSocketSubject<any>('ws://192.168.45.6:6000/');
    this._channel.subscribe(
      (data) => {
        this.reply = data;
      },
      (error) => {
        console.error('WebSocket error:', error);
      }
    );
    if (this.cameraUrl.trim() !== '') {
      this._channel.next(this.cameraUrl);
    }
  }
}
