import { Component } from '@angular/core';
import { User } from '../model/User';
import { UserService } from 'src/app/services/User.service';
import { HttpErrorResponse } from '@angular/common/http';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';
@Component({
  selector: 'app-mdp',
  templateUrl: './mdp.component.html',
  styleUrls: ['./mdp.component.css']
})
export class MdpComponent {
  constructor( private service: UserService, private router:Router, private authService: AuthService) { }
  listUser!: User[];

  email:string="";
  numTel:string="";
  id!:number
  login(){
    let email=this.email;
    id

    if(this.email==""||this.numTel==""||this.id==null){
      alert("your email or numTel is invalid");
    }
    else{

      this.service.recupererMDP(this.email,this.numTel).subscribe((mdp: number) => {
        this.id = mdp; // Assurez-vous que "this.id" est du bon type, potentiellement aussi un Observable<number>
    });
      console.log(this.id);
      this.router.navigate(['update',this.id]);
      this.authService.setLoggedIn(true); // Notification de connexion réussie
    }
  }

 ngOnInit(){


   }   
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Login Page</title>
  <link rel="stylesheet" href="mdp.component.css">
</head>
<body>
  <div class="background"></div>
  <div class="login-container">
    <form class="login-form">
      <h3>Password recovery</h3>
      <div class="form-group">
        <label for="email"> Email Adresse  </label>
        <input type="email" id="email" name="email" placeholder="Enter your email adresse " [(ngModel)]="email" required>
      </div>
      <div class="form-group">
        <label for="numTel"> phone number</label>
        <input type="numTel" id="numTel" name="numTel" placeholder="Specify your phone number" [(ngModel)]="numTel" required>
      </div>
      <br>
      <button type="submit" (click)="login()">Login</button>

    </form>
  </div>
</body>
</html>
