<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Register Page</title>
  <link rel="stylesheet" href="register.component.css">
</head>
<body>
  <div class="register-container" style="margin-top: 100px;">
    <form [formGroup]="RegisterForm" (ngSubmit)="onSubmit()">
      <h2>Register</h2>
      <div class="form-group">
        <label for="nom">Last Name <span>(Specify your last name)</span></label>
        <input type="text" id="nom" name="nom" placeholder="Enter your last name" formControlName="nom" required>
        <div *ngIf="this.RegisterForm.controls['nom'].invalid && (this.RegisterForm.controls['nom'].dirty || this.RegisterForm.controls['nom'].touched)">
          <div *ngIf="this.RegisterForm.controls['nom'].hasError('required')">Last name is required</div>
          <div *ngIf="this.RegisterForm.controls['nom'].hasError('minlength')">Last name should be at least 3 characters </div>
        </div>
      </div>
      <div class="form-group">
        <label for="prenom">First Name <span>(Specify your first name)</span></label>
        <input type="text" id="prenom" name="prenom" placeholder="Enter your first name" formControlName="prenom" required>
        <div *ngIf="this.RegisterForm.controls['prenom'].invalid && (this.RegisterForm.controls['prenom'].dirty || this.RegisterForm.controls['prenom'].touched)">
          <div *ngIf="this.RegisterForm.controls['prenom'].hasError('required')">First name is required</div>
          <div *ngIf="this.RegisterForm.controls['prenom'].hasError('minlength')">First name should be at least 3 characters </div>
        </div>
      </div>
      <div class="form-group">
        <label for="email">Email Address <span>(Specify your email address)</span></label>
        <input type="email" id="email" name="email" placeholder="Enter your email address" formControlName="email" required>
        <div *ngIf="this.RegisterForm.controls['email'].invalid && (this.RegisterForm.controls['email'].dirty || 
        this.RegisterForm.controls['email'].touched)">
          <div *ngIf="this.RegisterForm.controls['email'].hasError('required')">Email  is required</div>
          <div *ngIf="this.RegisterForm.controls['email'].hasError('email')">Email must respect the rules </div>
        </div>
      </div>
      <div class="form-group">
        <label for="password">Password <span>(Specify your password)</span></label>
        <input type="password" id="password" name="password" placeholder="Enter your password"formControlName="password" required>
        <div *ngIf="this.RegisterForm.controls['password'].invalid && (this.RegisterForm.controls['password'].dirty || 
        this.RegisterForm.controls['password'].touched)">
          <div *ngIf="this.RegisterForm.controls['password'].hasError('required')">Password  is required</div>
          <div *ngIf="this.RegisterForm.controls['password'].hasError('minlength')">Too short</div>
          <div *ngIf="this.RegisterForm.controls['password'].hasError('pattern')">
            Password  must contain at least one lowercase letter and one uppercase letter and a number </div>
        </div>
      </div>
      <div class="form-group">
        <label for="telephone">Phone Number <span>(Specify your phone number)</span></label>
        <input type="tel" id="telephone" name="telephone" placeholder="Enter your phone number"  formControlName="telephone"required>
        <div *ngIf="this.RegisterForm.controls['telephone'].invalid && (this.RegisterForm.controls['telephone'].dirty || 
        this.RegisterForm.controls['telephone'].touched)">
          <div *ngIf="this.RegisterForm.controls['telephone'].hasError('required')">Telephone  is required</div>
          <div *ngIf="this.RegisterForm.controls['telephone'].hasError('pattern')">Telephone should be 8 numbers </div>
        </div>
      </div>
      <div class="form-group">
        <label for="role">Role <span>(Select your role)</span></label>
        <select id="role" name="role" (change)="onRoleChange($event)"required>
          <option value="" disabled selected>Choose a role</option>
          <option value="super_admin">Super Admin</option>
          <option value="admin_club">Club Admin</option>
          <option value="user">User</option>
        </select>
      </div>
      <div class="form-group">
        <label for="interet">Interest <span>(Select your interest)</span></label>
        <select id="interet" name="interet" (change)="onInterestChange($event)" required>
          <option value="" disabled selected>Choose an interest</option>
          <option value="sport">Sport</option>
          <option value="music">Music</option>
          <option value="tech">Technology</option>
          <option value="photographie">Photography</option>
          <option value="design">Design</option>
          <option value="dessin">Drawing</option>
          <option value="robots">Robots</option>
        </select>
      </div>
      <button type="submit" routerLink="/profile" >Register</button>
    </form>
  </div>
</body>
</html>
