import { Component, OnDestroy } from '@angular/core';
import { WebSocketSubject } from 'rxjs/webSocket';
@Component({
  selector: 'app-ia',
  templateUrl: './ia.component.html',
  styleUrls: ['./ia.component.css']
})
export class IAComponent  {
  public _controller!:string;
  public  _channel: WebSocketSubject<any> = new WebSocketSubject({
    url: 'ws://localhost:4200/ia'
    //url: 'http://192.168.51.221:8080/video'
  });

  constructor() {}

  onPressed() {

      this._sendMessage();

  }

  private _sendMessage() {
    if (this._controller !== '') {
      this._channel.next(this._controller);
    }
  }

}
