import { Component } from '@angular/core';
import { WebSocketSubject } from 'rxjs/webSocket';
@Component({
  selector: 'app-ia',
  templateUrl: './ia.component.html',
  styleUrls: ['./ia.component.css']
})
export class IAComponent {


  private _controller: HTMLInputElement = new HTMLInputElement();
  private _channel: WebSocketSubject<any> = new WebSocketSubject({
    url: 'ws://10.0.2.2:6000/'
    //url: 'http://192.168.51.221:8080/video'
  });

  constructor() {}

  onPressed() {

      this._sendMessage();

  }

  private _sendMessage() {
    if (this._controller.value !== '') {
      this._channel.next(this._controller.value);
    }
  }

  ngOnDestroy() {
    this._channel.complete();
  }
}
