import { Component } from '@angular/core';
import { User } from '../model/User';
import { UserService } from 'src/app/services/User.service';
import { HttpErrorResponse } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor( private service: UserService, private router:Router) { }
  listUser!: User[];
  email!:string;
  motDePasse!:string;


 ngOnInit(){
     this.service.getAll().subscribe(
       { next: (data) => this.listUser=data,
         error: (err) => console.log(err),
         complete: () => console.log('done')
       });


   }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Login Page</title>
  <link rel="stylesheet" href="login.component.css">
</head>
<body>
  <div class="background"></div>
  <div class="login-container">
    <form class="login-form">
      <h2>Welcome Back!</h2>
      <div class="form-group">
        <label for="email"> Email Adresse  </label>
        <input type="email" id="email" name="email" placeholder="Enter your email adresse " [(ngModel)]="email" required>
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input type="password" id="password" name="password" placeholder="Enter your password"  [(ngModel)]="motDePasse"required>
        <a href="#" class="forgot-password">Forgot Password?</a>
      </div>
      <button type="submit">Login</button>
      <p class="register-link" routerLink="/register">Don't have an account? <a class="nav-link" routerLink="/register">Register</a></p>
    </form>
  </div>
</body>
</html>
