import { Component } from '@angular/core';
import { User } from '../model/User';
import { UserService } from 'src/app/services/User.service';
import { HttpErrorResponse } from '@angular/common/http';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';
@Component({
  selector: 'app-ia',
  templateUrl: './ia.component.html',
  styleUrls: ['./ia.component.css']
})
export class IAComponent {


  private _controller: HTMLInputElement = new HTMLInputElement();
  private _channel: WebSocketSubject<any> = new WebSocketSubject({
    url: 'ws://10.0.2.2:6000/'
    //url: 'http://192.168.51.221:8080/video'
  });

  constructor() {}

  onPressed() {
    if (this.j === 0) {
      this._sendMessage();
    }
  }

  private _sendMessage() {
    if (this._controller.value !== '') {
      this._channel.next(this._controller.value);
    }
  }

  ngOnDestroy() {
    this._channel.complete();
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Login Page</title>
  <link rel="stylesheet" href="ia.component.css">
</head>
<body>
  <div class="background"></div>
  <div class="login-container">
    <form class="login-form">
      <h2>Welcome Back!</h2>
      <div class="form-group">
        <label for="email"> Email Adresse  </label>
        <input type="email" id="email" name="email" placeholder="Enter your email adresse " [(ngModel)]="email" required>
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input type="password" id="password" name="password" placeholder="Enter your password"  [(ngModel)]="motDePasse"required>
        <a routerLink="/mdp"class="forgot-password">Forgot Password?</a>
      </div>
      <button type="submit" (click)="login()">Login</button>
      <p class="register-link" routerLink="/register">Don't have an account? <a class="nav-link" routerLink="/register">Register</a></p>
    </form>
  </div>
</body>
</html>
