import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators ,FormBuilder} from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  constructor(private formBuilder: FormBuilder) { }
  RegisterForm = new FormGroup({
    nom : new FormControl('',[Validators.required,Validators.minLength(3)]),
  prenom : new FormControl('',[Validators.required,Validators.minLength(3)]),
  email : new FormControl('',[Validators.required,Validators.email]),
  password : new FormControl('',[Validators.required,Validators.minLength(3),
    Validators.pattern('/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).+$/')]),
  telephone : new FormControl('',[Validators.required,Validators.pattern('[0-9]{8}')]),
  role : new FormControl('',[Validators.required]),
  interet : new FormControl('',[Validators.required]),
  });

  ngOnInit(): void {

  }

  onSubmit() {
    if (this.RegisterForm.valid) {
      // Do something when the form is submitted
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Register Page</title>
  <link rel="stylesheet" href="register.component.css">
</head>
<body>
  <div class="register-container" style="margin-top: 100px;">
    <form [formGroup]="RegisterForm" (ngSubmit)="onSubmit()">
      <h2>Register</h2>
      <div class="form-group">
        <label for="nom">Last Name <span>(Specify your last name)</span></label>
        <input type="text" id="nom" name="nom" placeholder="Enter your last name" formControlName="nom" required>
        <div *ngIf="RegisterForm.get('nom').invalid && (RegisterForm.get('nom').dirty || RegisterForm.get('nom').touched)">
          <div *ngIf="RegisterForm.get('nom').errors?.required">Last name is required</div>
          <div *ngIf="RegisterForm.get('nom').errors?.minlength">Last name should be at least 3 characters long</div>
        </div>
      </div>
      <!-- Repeat similar structure for other form fields -->
      <button type="submit">Register</button>
    </form>
  </div>
</body>
</html>
