import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { User } from '../model/User';
import { UserService } from 'src/app/services/User.service';
@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  id!:number
  user!:User
  constructor(private Act : ActivatedRoute,private service: UserService) {}
  ngOnInit(){
    this.id = this.Act.snapshot.params['id'];
    this.service.getUserById(this.id).subscribe(
{
     next: (data:any)=>{this.user=data
      console.log("photoooooooo",data)
     },
      error: (err) => console.log(err),
      complete: () => console.log('done')
    });
   // console.log(this.id);
  }
}

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>User Profile</title>
    <link rel="stylesheet" href="profile.component.css">
</head>
<body>
    <div class="profile-container" style="margin-top: 120px;">
        <div class="profile-header">
            <div class="profile-picture">
                <!-- Placeholder for profile picture -->
                <img [src]="'../../assets/img/'+user.photo" alt="">
            </div>
            <div class="profile-info">
                <div class="button-container">
                    <h1 class="username">{{user.nomUtilisateur}} {{user.prenomUtilisateur}}</h1>

                </div>
            </div>
        </div>
        <div class="profile-details">
            <div class="detail">
                <h2>Interest</h2>
                <ul>
                    <li>{{user.interet}}</li>
                </ul>
            </div>
            <div class="detail">
                <h2>Contact Information</h2>
                <p>Email: {{user.email}}</p>
                <p>Phone: {{user.numTel}}</p>
                <button class="edit-button" [routerLink]="['/update',user.id]">Edit</button>
            </div>
        </div>
    </div>
</body>
